import React, { Component } from 'react';
import { TextInput, StyleSheet, View, ScrollView, Button, Image, KeyboardAvoidingView, ActivityIndicator, Alert } from 'react-native';
import logo from './logo.svg';
import './Screen1.css';

export default class Screen1 extends Component {
    constructor(props) {
        super(props);
    }

    state = {
        response: ''
    };


  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/mensagem');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
      <div className="Screen1">
            <header className="Screen1-header">
                <img src={logo} className="Screen1-logo" alt="logo" />
                <h1 className="Screen1-title">Bem vindo ao App React</h1>
        </header>
            <p className="Screen1-intro">{this.state.response}</p>
      </div>
    );
  }
}

const styles = StyleSheet.create({
    container: {
        //paddingRight: 10,
        //paddingLeft: 10,
        backgroundColor: '#2C1526',
    },
    input: {
        paddingLeft: 5,
        paddingRight: 5,
    },
    btn: {
        paddingTop: 20,
        fontSize: 11,
    },
    loading: {
        padding: 20,
    }
});
