import React, { Component } from 'react';
import { TextInput, StyleSheet, View, Button, ActivityIndicator } from 'react-native';
import logo from './logo.svg';
import './Screen1.css';

export default class Screen1 extends Component {
    constructor(props) {
        super(props);

        this.state = {
            min: '',
            max: '',
            isLoading: false,
            message: "",
            response: '',
            post: '',
            responseToPost: '',
        };
    }

    onChangeHandler(field, value) {
        this.setState({ [field]: value });
    }

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/mensagem');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
    };

    

  renderButton() {
    if (this.state.isLoading)
        return <ActivityIndicator size="large" style={styles.loading} />;

    return (
            <View style={styles.btn}>
                <Button
                    title='SORTEAR'
                    color='#6542f4'
                onPress={() => this.handleSubmit}
                />
            </View>
          )
    }

    handleSubmit = async e => {
        e.preventDefault();
        const response = await fetch('/api/nums', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ post: this.state.post }),
        });
        const body = await response.text();

        this.setState({ responseToPost: body });
    };
    /*
    gimmaNumbers() {
        const { min, max } = this.state;

       // e => this.setState({ post: e.target.value })

        if (!min || !max) {
            Alert.alert(
                "Para calcular informe ambos os valores"
            );
            return null;
        }
        Alert.alert(
            "Deseja prosseguir o calculo com os valores informados?",
            [{
                text: "CANCELAR",
                style: 'cancel'
            },
            {
                text: "PROSSEGUIR",
                onPress: () => {
                    this.handleSubmit
                }
                //onChange={e => this.setState({ post: e.target.value })}
            }],
        );

    }
    */
    acessarApp() {
        this.setState({ isLoading: false });
        this.props.navigation.replace('Habitos');
    }

  render() {
    return (
      <div className="Screen1">
        <header className="Screen1-header">
                <img src={logo} className="Screen1-logo" alt="logo" />
                <h4 className="Screen1-title">Welcome to React App</h4>
        </header>
            <p className="Screen1-intro">{this.state.response}</p>

            <View style={styles.container}>
                <TextInput
                    style={styles.input}
                    placeholder="From: (min number)"
                    value={this.state.min}
                    onChangeText={value => this.onChangeHandler('min', value)}
                />
            </View>
            <form onSubmit={this.handleSubmit}>
                <input
                    placeholder="To: (max number)"
                    type="text"
                    value={this.state.post}
                    onChange={e => this.setState({ post: e.target.value })}
                />
                <button type="submit">Submit</button>
            </form>
            
            <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}
