        value={this.state.post}
        onChange={e => this.setState({ post: e.target.value })}
    />
    <button type="submit">Submit</button>
</form>
<p>{this.state.responseToPost}</p>
*/
const styles = StyleSheet.create({
    container: {
        padding: 10,
        backgroundColor: 'white',
        marginTop: 5,
        marginBottom: 5,
        elevation: 0.5,
    },
    scrv: {
        backgroundColor: '#2C1526',
    },
    input: {
        paddingLeft: 5,
        paddingRight: 5,
    },
    btn: {
        paddingTop: 20,
        fontSize: 11,
    },
    loading: {
        padding: 20,
    }
});
