import React from 'react';
import {View} from 'react-native';
import { Badge } from 'reactstrap';
import logo from './logo.svg';
import './Screen1.css';

export default class Screen2 extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            post: '',
            Sort: '',
            response: '',
            resp2: '',
        };
    }

    componentDidMount() {
        this.callApi()
            .then(res => this.setState({ resp2: res.express }))
            .catch(err => console.log(err));

    }

    callApi = async () => {
        const response = await fetch('/api/sort');
        const body = await response.json();
        if (response.status !== 200) throw Error(body.message);

        return body;
    };

    render() {
        return (
            <>
                <div className="Screen1">
                    <header className="Screen1-header">
                        <img src={logo} className="Screen1-logo" alt="logo" />
                        <h4 className="Screen1-title">Numero Sorteado</h4>
                    </header>

                    <p className="Screen1-intro">{this.state.response}</p>
                    <View>
                        <Badge
                        value={this.state.resp2}
                        textStyle={{ color: 'orange' }}
                        />
                    </View>
                </div>
            </>
        );
    }
};
